import React, { useEffect, useState } from "react";
import './App.css';
import Heading from "./Heading";
import Recepe from "./Recepe";
import axios from "axios";

const App=()=> {

  const[val,setval]=useState();
  const[val_2,setval_2]=useState();
  const[fet,setfet]=useState([]);
  const Change=(e)=>{
    setval(e.target.value);
  }
  const Click=()=>{
    setval_2(val);
  }



  const FetchData=async()=>{

    try{const res=await axios.get(`https://api.edamam.com/api/recipes/v2?type=public&q=${val_2}&app_id=abc36306&app_key=239e03d22a3c5b1829557fc496f66be7`);
    const data=await res.json();}
    catch(e){
      <h1>e</h1>
    }

    setfet((oldfetch)=>{
      return[
           ...oldfetch,data.hits
      ];
    });
    console.log(fet);

  }

  useEffect(()=>{
    const FetchData=async()=>{
      const res=await fetch(`https://api.edamam.com/api/recipes/v2?type=public&q=${val_2}&app_id=abc36306&app_key=239e03d22a3c5b1829557fc496f66be7`);
      const data=await res.json();

      setfet((oldfetch)=>{
        return[
             ...oldfetch,data.hits
        ];
      });
      console.log(fet);

    }
    FetchData();

  },[val_2]);

  return (
  <>
  <Heading val={val} Click={Click} Change={Change} val_2={val_2}  />
  <div className="container-fluid"></div>
  <Recepe fet={fet}/>

  </>
  );
}

export default App;
